import React, { useState } from "react";
import axios from "axios";
import { axiosWithAuth } from "../utilities/axiosWithAuth";

const initialColor = {
  color: "",
  code: { hex: "" }
};

const ColorList = ({ colors, updateColors }) => {
  console.log(colors);
  const [editing, setEditing] = useState(false);
  const [colorToEdit, setColorToEdit] = useState(initialColor);
  const [newColor, setNewColor] = useState(initialColor);

  const editColor = color => {
    setEditing(true);
    setColorToEdit(color);
  };


  const saveEdit = e => {
    e.preventDefault();
    // Make a put request to save your updated color
    // think about where will you get the id from...
    // where is is saved right now?
    axiosWithAuth().put(`http://localhost:5000/api/colors/${colorToEdit.id}`, colorToEdit)
    .then(response => {
      console.log(response)
      axiosWithAuth().get('http://localhost:5000/api/colors')
      .then(response => {
        updateColors(response.data)
      })
      .catch(error => console.log(error))
    })
    .catch(error => console.log(error))
  };

  const deleteColor = color => {
    // make a delete request to delete this color
    axiosWithAuth().delete(`http://localhost:5000/api/colors/${color.id}`)
    .then(response => {
      console.log(response)
      updateColors(colors.filter(colorId => colorId.id !== color.id ))
    })
    .catch(error => console.log(error))
  };


  return (
    <div className="colors-wrap">
      <p>colors</p>
      <ul>
        {colors.map(color => (
          <li key={color.color} onClick={() => editColor(color)}>
            <span>
              <span className="delete" onClick={e => {
                    e.stopPropagation();
                    deleteColor(color)
                  }
                }>
                  x
              </span>{" "}
              {color.color}
            </span>
            <div
              className="color-box"
              style={{ backgroundColor: color.code.hex }}
            />
          </li>
        ))}
      </ul>
      {editing && (
        <form onSubmit={saveEdit}>
          <legend>edit color</legend>
          <label>
            color name:
            <input
              onChange={e =>
                setColorToEdit({ ...colorToEdit, color: e.target.value })
              }
              value={colorToEdit.color}
            />
          </label>
          <label>
            hex code:
            <input
              onChange={e =>
                setColorToEdit({
                  ...colorToEdit,
                  code: { hex: e.target.value }
                })
              }
              value={colorToEdit.code.hex}
            />
          </label>
          <div className="button-row">
            <button type="submit">save</button>
            <button onClick={() => setEditing(false)}>cancel</button>
          </div>
        </form>
      )}
      <div className="spacer" />
      <form>
        <label htmlFor='color'>Color: </label>
        <input type='text' name='color' id='color' value={newColor.color} onChange={(event) => {
          setNewColor({
            ...newColor,
            color: event.target.value
          })
        }} />
        <label htmlFor='hex'>Hex No. </label>
        <input type='text' name='hex' id='hex' value={newColor.code.hex} onChange={(event) => {
          setNewColor({
            ...newColor,
            code: { hex: event.target.value}
          })
        }} />
      </form>
    </div>
  );
};

export default ColorList;
